import React from "react";
import { useState } from "react";
import { BrowserRouter,useNavigate } from 'react-router-dom';

const Login=()=>{

const [newUseState,setNewUseState]=useState(false)
const [userID,setUserId]=useState(0)
const[data,setdata]=useState([])
const [logIn,setLogIn]=useState(false)
const navigate =useNavigate()



    const posting = async()=>{


        await fetch("http://localhost:3001/user",{
          method:"POST",
          headers: {'Content-Type': 'application/json'},
          body: JSON.stringify({
            Firstname: document.getElementById("Firstname").value,
            Lastname:document.getElementById("lastname").value,
            username:document.getElementById("username").value,
            password:document.getElementById("password").value,
          })
        })
        .then(res=>res.json())
        .then(fetch("http://localhost:3001/user")
        .then(res=>res.json())
        .then(data=>setdata(data))
        .then(data.map(elem=>{
          if(elem.username==document.getElementById("username").value){
            setNewUseState(data.id)
          }
      }))
        .then(navigate(`/:${userID}`,{state:userID}))


        )
      }

      const loggingIn=async()=>{
       await fetch("http://localhost:3001/user")
        .then(res=>res.json())
        .then(data=>setdata(data))
        console.log(data)
        let user=data.map(elem=> {
          if((elem.username==document.getElementById("username").value)&&(elem.password==document.getElementById("password").value)){
            return elem
          }
        })
        console.log("user",user)


      }



    return (
        <>
        <button id="new-user" onClick={async()=>await setNewUseState(true) }>New User</button>
        <button id="sign-in" onClick={async()=>await setLogIn(true) }>Sign in</button>
        {logIn && (
          <>
        <form >

        <label> Enter username:
        <input type='text' id='Firstname' placeholder='username' />
        </label>
        <label>Enter password
        <input type='text' id='lastname' placeholder='password' />
        </label>
        </form>
        <button id='logIn' onClick={()=>loggingIn()}>Login</button>
        </>
        )
        }
        {newUseState && (
          <>
        <form >

        <label> Enter Firstname:
        <input type='text' id='Firstname' placeholder='enter Firsntame' />
        </label>
        <label>Enter lastname
        <input type='text' id='lastname' placeholder='enter Lastname' />
        </label>
        <label>Enter username:
        <input type='text' id='username' placeholder='enter username' />

        </label>
        <label>Enter password:
        <input type='text' id='password' placeholder='enter password' />

        </label>
        </form>
        <button id='form-submit' onClick={()=>posting()}>Submit</button>
        </>
        )
        }
        </>
    )







}
  export default Login
