import React from "react";

import { useState,useEffect,useContext } from "react";
import EditItem from "./editItem";
import ReactModal from 'react-modal';

const Home = () => {

//     console.log("out of use")

  const [items,setItems]=useState([])
  const [item,setItem]=useState({})
  const [show,setShow]=useState(false)



  useEffect(()=>{
    console.log("here")
    fetch("http://localhost:3001")
    .then(res=>res.json())
    .then(data=>setItems(data))
  },[])




  const posting = async()=>{


    await fetch("http://localhost:3001",{
      method:"POST",
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        item_name: document.getElementById("item-name").value,
        description:document.getElementById("item-description").value,
        quantity:document.getElementById("item-quantity").value
      })
    })
    .then(res=>res.json())
  }

  const deleting = async(item) =>{
      console.log("deleteing",item)
      await fetch("http://localhost:3001",{
      method:"DELETE",
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(item)
  })
  .then(res=>res.json())
}


const editing= async(item)=>{
  console.log("editing",item)
  if(show)
  {
     await setShow(false)

  }
  else{
    console.log("else")
    return(
    <div>
    {show &&(
      <ReactModal isOpen="true">
       <div id='editing'>
         This is the content of the pop-up.

         <form id='editForm' >
         <label>Enter the item name:
         <input type='text' id='item-nameE'placeholder={item.item_name} ></input>

         </label>
         <label> Enter item description:
         <input type='text' id='item-descriptionE' value={item.description} />
         </label>
         <label>Enter quantity
         <input  type='text' id='item-quantityE' placeholder={item.quantity} />
         </label>

       </form>

         <button onClick={ async() => editing(show)}>
         Close
        </button>

       </div>
       </ReactModal>
     )}
     </div>
    )
  }
}




const itemMap=()=>{
return(
  items.map(item=>
    <>
  <div onClick={()=>deleting(item)}>Delete</div>
  <div id='item' onClick={async ()=> {await setShow(true); await editing(item)}}>Edit</div>



  <div>

    {item.item_name}{item.description}{item.quantity}
  </div>
  </>
  )

)
}


return(

<>
  <form >
  <label>Enter the item name:
  <input type='text' id='item-name' placeholder='enter item name' />

  </label>
  <label> Enter item description:
  <input type='text' id='item-description' placeholder='enter item description' />
  </label>
  <label>Enter quantity
  <input type='text' id='item-quantity' placeholder='enter item quantity' />
  </label>

</form>

<button id='form-submit' onClick={()=>posting()}>Submit</button>
  {itemMap()}
</>
)
}


export default Home
